//import React from 'react';
import React, { Component } from 'react';

class Person  extends Component {
  state= {
      name: null,
      age: null,
      birthMonth: null
  }
    handleSubmit= (e) => {
          e.preventDefault();
          this.props.addInfo(this.state);   // We are accessing the function as a prop we passed from the parent component.
}
render(){
  return(

      <div className='center'>
        <form onSubmit={this.handleSubmit}>
      <h4 className='container center'>Person Information</h4>
      <br/ >
      <h6 className='changes' >
      <label htmlFor='name' > Name </label><input type="text" />
      Birth Date:<input type="text"/>
      <br/>
      <br/>
      About Me:
      <br/><br/><textarea id="area">
      </textarea>
      <br/>
      <br/>
      <br/>
      <input type="Submit"/>
      </h6>
      </form>
      </div>
  )
}
}
export default Person;
