//import React from 'react';
import React, { Component } from 'react';

class Person  extends Component {
  state= {
      name: null,
      age: null,
      birthMonth: null
  }

  handleChange= (e) => {
    this.setState({
      [e.target.id]: e.target.value
    }
    )
  }
    handleSubmit= (e) => {
          e.preventDefault();
          this.props.Person(this.state);   // We are accessing the function as a prop we passed from the parent component.
}
render(){
  return(

      <div className='center'>
        <form onSubmit={this.handleSubmit}>
      <h4 className='container center'>Person Information</h4>
      <br/ >
      <h6 className='changes' >
      <label htmlFor='name' > Name </label>
      <input type="text" id="name"  onChange={this.handleChange}/>
      <label htmlFor='age' > Birth Date: </label>
      <input type="text" id="age"  onChange={this.handleChange}/>
      <br/>
      <br/>
      <label htmlFor='about' >About Me:</label>
      <br/><br/><textarea id="about"  onChange={this.handleChange}>
      </textarea>
      <br/>
      <br/>
      <br/>
      <input type="Submit"/>
      </h6>
      </form>
      </div>
  )
}
}
export default Person;
